import {makeStyles} from '@mui/styles';
//import NavigableContainer from "../containers/NavigableContainer";
//import InstructorForm from "../forms/InstructorForm";
import React, { Component }  from 'react';
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";



const NewInstructorView = ({handleChange, handleSubmit}) => {
const instructors = useStyles();
return (
  <div>
        <h1>New Instructor</h1>

        <div className={instructors.root}>
          <div className={instructors.formContainer}>
            <div className={instructors.formTitle}>
              <Typography
                style={{
                  fontWeight: "bold",
                  fontFamily: "Courier, sans-serif",
                  fontSize: "20px",
                  color: "#11153e",
                }}
              >
                Add a Instructor
              </Typography>
            </div>
            <form
              style={{ textAlign: "center" }}
              onSubmit={(e) => handleSubmit(e)}
            >
              <label style={{ color: "#11153e", fontWeight: "bold" }}>
                School Name:{" "}
              </label>
              <input
                type="text"
                name="name"
                onChange={(e) => handleChange(e)}
                required
              />
              <br />
              <br />

              <label style={{ color: "#11153e", fontWeight: "bold" }}>
                School Address:{" "}
              </label>
              <input
                type="text"
                name="address"
                onChange={(e) => handleChange(e)}
                required
              />
              <br />
              <br />

              <label style={{ color: "#11153e", fontWeight: "bold" }}>
                Description:{" "}
              </label>
              <input
                type="text"
                name="description"
                onChange={(e) => handleChange(e)}
              />
              <br />
              <br />

              <Button variant="contained" color="primary" type="submit">
                Submit
              </Button>
              <br />
              <br />
            </form>
          </div>
        </div>
      </div>
);
}

const useStyles = makeStyles(() => ({
    formContainer: {
        width: '500px',
        backgroundColor: '#61ffa8',
        borderRadius: '5px',
        margin: 'auto',
    },
    title: {
        flexGrow: 1,
        textAlign: 'left',
        textDecoration: 'none'
    },
    customizeAppBar: {
        backgroundColor: '#11153e',
        shadows: ['none'],
    },
    formTitle: {
        backgroundColor: '#261c69',
        marginBottom: '15px',
        textAlign: 'center',
        borderRadius: '5px 5px 0px 0px',
        padding: '3px'
    },
    labelStyle: {
        color: '#9c40f7',
        fontWeight: 'bold'
    },
}));


export default NewInstructorView;
