import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";

const EditInstructorView = (props) => {
  const { instructor, handleChange, handleSubmit } = props;

  const courses = useStyles();
  return (
    <div>
      <h1>Edit Instructor</h1>
      <div className={courses.root}>
        <div className={courses.courseFormContainer}>
          <div className={classes.courseFormTitle}>
            <Typography
              style={{fontWeight: "bold", fontSize: "20px",color: "white",fontFamily: "Courier, sans-serif",}}>
              Edit an Instructor
            </Typography>
          </div>
          <form
            style={{ textAlign: "center" }}
            onSubmit={(e) => handleSubmit(e)}
          >
            <label style={{ color: "black", fontWeight: "bold" }}>
              School Name:{" "}
            </label>
            <input
              type="text"
              name="name"
              onChange={(e) => handleChange(e)}
              value={campus.name}
              required
            />
            <br />
            <br />

            <label style={{ color: "black", fontWeight: "bold" }}>
              School Address:{" "}
            </label>
            <input
              type="text"
              name="address"
              onChange={(e) => handleChange(e)}
              value={campus.address}
              required
            />
            <br />
            <br />
            <label style={{ color: "black", fontWeight: "bold" }}>
              School ImageUrl:{" "}
            </label>
            <input
              type="text"
              name="imageUrl"
              onChange={(e) => handleChange(e)}
              value={campus.imageUrl}
              required
            />
            <br />
            <br />

            <label style={{ color: "#11153e", fontWeight: "bold" }}>
              Description:{" "}
            </label>
            <input
              type="text"
              name="description"
              onChange={(e) => handleChange(e)}
              value={campus.description}
              required
            />
            <br />
            <br />

            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
            <br />
            <br />
          </form>
        </div>
      </div>
    </div>
  );
};

const useStyles = makeStyles(() => ({
  courseFormContainer: {
    width: "500px",
    backgroundColor: "#f0f0f5",
    borderRadius: "5px",
    margin: "auto",
  },
  courseFormTitle: {
    backgroundColor: "#7d0aff",
    marginBottom: "15px",
    textAlign: "center",
    borderRadius: "5px 5px 0px 0px",
    padding: "3px",
  },
}));



export default EditInstructorView;
