import {
  ADD_STUDENT,
  DELETE_INSTRUCTOR,
  DELETE_STUDENT,
  EDIT_INSTRUCTOR,
  EDIT_STUDENT,
  FETCH_INSTRUCTOR
} from "../actions/actionTypes";
import students from "./students";

const initialState = {
  students: [],
};

const instructor = (state = initialState, action) => {

  switch (action.type) {
    case FETCH_INSTRUCTOR:
    case EDIT_INSTRUCTOR:
        return action.payload;
    case DELETE_INSTRUCTOR:
        return initialState;
    case ADD_COURSE:
        return {
            ...state,
            courses: courses(state.courses, action.payload)
        }
    case DELETE_COURSE:
        return {
            ...state,
            courses: courses(state.courses, action.payload)
        }
    case EDIT_COURSE:
        if (state.students.some(course => course.id === action.payload.id))
            if (state.id !== action.payload.instructorId)
                return instructor(state, {
                    payload: action.payload.id,
                    type: DELETE_STUDENT });
            else
                return {
                    ...state,
                    students: students(state.students, action)
                };
        else if (state.id === action.payload.instructorId)
            return instructor(state, {...action, type: ADD_STUDENT})
        else
            return state;
    default:
        return state;
}
};

export default instructor;
