/*==================================================
AllcoursesView.js
The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the all courses view page.
================================================== */
import { Link } from "react-router-dom";

const AllCoursesView = (props) => {
  const {courses, deletecourse} = props;
  // If there is no course, display a message
  if (!courses.length) {
    return (
    <div>
      <p>There are no courses.</p>
      <Link to={`newcourse`}>
        <button>Add New course</button>
      </Link>
    </div>
    );
  }

  // If there is at least one course, render All courses view
  return (
    <div>
      <h1>All courses</h1>

      {courses.map((course) => {
          let name = course.firstname + " " + course.lastname;
          return (
            <div key={course.id}>
              <Link to={`/course/${course.id}`}>
                <h2>{name}</h2>
              </Link>
              <button onClick={() => deletecourse(course.id)}>Delete</button>
              <hr/>
            </div>
          );
        }
      )}
      <br/>
      <Link to={`/newcourse`}>
        <button>Add New course</button>
      </Link>
      <br/><br/>
    </div>
  );
};


export default AllcoursesView;
